CREATE DATABASE IF NOT EXISTS Shoeshop;
USE Shoeshop;

-- Tables
CREATE TABLE accounts (
    id INT AUTO_INCREMENT PRIMARY KEY,
    email VARCHAR(255) NOT NULL,
    display_name VARCHAR(500) COLLATE utf8mb4_unicode_ci,
    password VARCHAR(255) NOT NULL,
    role ENUM('ADMIN', 'CUSTOMER', 'SELLER') NOT NULL,
    full_name VARCHAR(500) COLLATE utf8mb4_unicode_ci,
    date_of_birth DATE,
    address VARCHAR(500) COLLATE utf8mb4_unicode_ci,
    phone_number VARCHAR(20),
    gender BOOLEAN,
    avatar_url VARCHAR(255),
    is_activated BOOLEAN DEFAULT TRUE,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME
);
CREATE TABLE constants (
    id INT AUTO_INCREMENT PRIMARY KEY,
    type VARCHAR(255) NOT NULL,
    value VARCHAR(500) COLLATE utf8mb4_unicode_ci,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME
);
CREATE TABLE refresh_tokens (
    id INT AUTO_INCREMENT PRIMARY KEY,
    account_id INT,
    token VARCHAR(255),
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    FOREIGN KEY (account_id) REFERENCES accounts(id)
);
CREATE TABLE products (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(500) COLLATE utf8mb4_unicode_ci,
	category_id INT,
    material_id INT,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    deleted_at DATETIME,
    FOREIGN KEY (category_id) REFERENCES constants(id),
    FOREIGN KEY (material_id) REFERENCES constants(id)
);
CREATE TABLE product_details (
    id INT AUTO_INCREMENT PRIMARY KEY,
    origin_price INT,
    discount INT,
    price INT,
    gender ENUM('MALE', 'FEMALE', 'UNISEX'),
    description JSON,
    product_id INT,
    color_id INT,
    style_id INT,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    deleted_at DATETIME,
    FOREIGN KEY (product_id) REFERENCES products(id),
    FOREIGN KEY (color_id) REFERENCES constants(id),
    FOREIGN KEY (style_id) REFERENCES constants(id)
);
CREATE TABLE product_images (
    id INT AUTO_INCREMENT PRIMARY KEY,
    product_detail_id INT,
    url VARCHAR(300),
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    FOREIGN KEY (product_detail_id) REFERENCES product_details(id)
);
CREATE TABLE product_quantities (
    id INT AUTO_INCREMENT PRIMARY KEY,
    product_detail_id INT,
    size_id INT,
    quantity INT,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    FOREIGN KEY (product_detail_id) REFERENCES product_details(id),
    FOREIGN KEY (size_id) REFERENCES constants(id)
);
CREATE TABLE shopping_carts (
    id INT AUTO_INCREMENT PRIMARY KEY,
    account_id INT,
    product_quantity_id INT,
    quantity INT,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    FOREIGN KEY (account_id) REFERENCES accounts(id),
    FOREIGN KEY (product_quantity_id) REFERENCES product_quantities(id)
);
CREATE TABLE orders (
    id INT AUTO_INCREMENT PRIMARY KEY,
    account_id INT,
    total_price DECIMAL,
    phone_number VARCHAR(20),
    address VARCHAR(500),
    status ENUM('WAIT', 'CONFIRM', 'REJECT', 'CANCEL', 'RECEIVED', 'DONE'),
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    FOREIGN KEY (account_id) REFERENCES accounts(id)
);
CREATE TABLE order_details (
    id INT AUTO_INCREMENT PRIMARY KEY,
    order_id INT,
    product_quantity_id INT,
    price INT,
    count INT,
    created_at DATETIME DEFAULT (now()),
    updated_at DATETIME,
    FOREIGN KEY (order_id) REFERENCES orders(id),
    FOREIGN KEY (product_quantity_id) REFERENCES product_quantities(id)
);

-- Index
CREATE INDEX idx_email ON accounts (email);
CREATE INDEX idx_product_name ON products (name);
CREATE INDEX idx_product_detail_price ON product_details (price);
CREATE INDEX idx_product_detail_gender_and_price ON product_details (gender, price);

